import NavBar from "../components/NavBar";
import Header from "../components/Header";
import NewActivityForm from "../components/NewActivityForm";
import Search from "../components/SearchFriends";
import UpdateFriend from "../components/UpdateFriend";
import { useState, useEffect } from "react";

function Activity() {

  const [activities, setActivities] = useState([])
  const [searchTerm, setSearchTerm] = useState("");

  useEffect(() =>{
    fetch("/activities")
      .then(r => r.json())
      .then(data => setActivities(data))
      .catch(error => console.error(error))
  }, [])

  const handleAddActivity = (newActivity) => {
    const updatedActivitiesArray = [...activities, newActivity];
    setActivities(updatedActivitiesArray);
  }

  const displayedFrens = activities.filter((fren) =>
  fren.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const arrayDataItems = displayedFrens.map((fren) => (
    <li key={fren.id}>
      <p>{fren.name}, {fren.location},{fren.type}</p>
    </li> ))

  return (
    <>
    <div class="flex">
    <div class="split w-1/2 ">
      <div class="centered">
      <h1 class=" max-w-sm mx-auto text-teal-300 text-xl">Add an activity!</h1>
      <span class=" font-medium text-slate-300 -translate-x-1/2 bg-white left-1/2 dark:text-white dark:bg-gray-300">What should we do?</span>
        <NewActivityForm onAddActivity={handleAddActivity}  />
        <hr class="w-48 h-1 mx-auto my-4 bg-gray-100 border-0 rounded md:my-10 dark:bg-gray-700"></hr>

      </div>
    </div>
    {/* <div class=" inset-y-0 left-1/2 w-0.5 bg-blue-500 opacity-30 divide-dotted"></div> */}

    <div class="split w-1/2 ">
      <div class="centered">
      <h1 class=" max-w-sm mx-auto text-teal-300 text-xl">Available activities!</h1>
      <span class=" font-medium text-slate-300 -translate-x-1/2 bg-white left-1/2 dark:text-white dark:bg-gray-300">Search Activities</span>

      <Search searchTerm={searchTerm} onSearchChange={setSearchTerm}/>
      <br></br>

        <div class="max-w-sm mx-auto">
        <ul>{arrayDataItems}</ul>
        </div>
      </div>
    </div>
  </div>
    </>
  )

};

export default Activity;
