import React, { useState } from 'react';

function UpdateFriend() {
    const [name, setName] = useState('');
    const [birthday, setBirthday] = useState('');
    const [favoriteColor, setFavoriteColor] = useState('');
    const [userId, setUserId] = useState('');
    const [id, setId] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const response = await fetch(`/friends/${id}`, {
                method: 'PATCH',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ name, birthday, favorite_color: favoriteColor, user_id: userId }),
            });
            if (!response.ok) {throw new Error('Failed to update friend record');}
            console.log('Friend record updated successfully');
        } catch (error) {console.error('Error updating friend record:', error.message);}
    };

    return (
        <div>
            <h1>Update Friend</h1>
            <form onSubmit={handleSubmit}>
                <label htmlFor="id">Friend ID:</label>
                <input
                    type="number"
                    id="id"
                    value={id}
                    onChange={(e) => setId(e.target.value)}
                    required
                /><br />
                <label htmlFor="name">Name:</label>
                <input
                    type="text"
                    id="name"
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                    required
                /><br />
                <label htmlFor="birthday">Birthday:</label>
                <input
                    type="date"
                    id="birthday"
                    value={birthday}
                    onChange={(e) => setBirthday(e.target.value)}
                /><br />
                <label htmlFor="favoriteColor">Favorite Color:</label>
                <input
                    type="text"
                    id="favoriteColor"
                    value={favoriteColor}
                    onChange={(e) => setFavoriteColor(e.target.value)}
                /><br />
                <label htmlFor="userId">User ID:</label>
                <input
                    type="number"
                    id="userId"
                    value={userId}
                    onChange={(e) => setUserId(e.target.value)}
                /><br />
                <button type="submit">Update Friend</button>
            </form>
        </div>
    );
}

export default UpdateFriend;
