import React from "react";
import { useFormik } from "formik";
// import { useFormik, Formik, Form, Field } from 'formik';
import * as yup from "yup";

function SignUp({ setUser }) {

  const formSchema = yup.object().shape({
    username: yup.string().email("Invalid email"),
    password: yup.string().required("Must be more than 2 characters").min(3),
    age: yup.number().positive().integer().required("Must enter age").typeError('Please enter an Integer').max(125),
  })

  const formik = useFormik({
    initialValues: {
      username: "",
      password: "",
    },
    onSubmit: (values) => {
      fetch("signup", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(values),
      }).then((res) => {
        if (res.ok) {
          res.json().then((user) => setUser(user)); // OR could make them still log in by redirecting to app "/" and not setting user
          window.location.href = "/home";
        }
      });
    },
  });

  return (
    <div>
      <form onSubmit={formik.handleSubmit}>
        <h1>Ben Sign Up</h1>
        <label htmlFor="username">Username</label>
        <input
          type="text"
          id="username"
          autoComplete="current-username"
          value={formik.values.username}
          onChange={formik.handleChange}
        />

        <label htmlFor="password">Password</label>
        <input
          type="password"
          id="password"
          autoComplete="current-password"
          value={formik.values.password}
          onChange={formik.handleChange}
        />
        <button type="submit">Sign Up</button>
      </form>
    </div>
  );
}

export default SignUp;
