import NavBar from "../components/NavBar";
import Header from "../components/Header";
import FriendCard from "../components/FriendCard";
import NewFriendForm from "../components/NewFriendForm";
import { useState, useEffect } from "react";

function Friends() {

  const [friends, setFriends] = useState([])

  useEffect(() =>{
    fetch("/friends")
      .then(r => r.json())
      .then(data => setFriends(data[0].friends))
      .catch(error => console.error(error))
  }, [])

  const handleAddFriend = (addFriend) => {
    const updatedFriendsArray = [...friends, addFriend];
    setFriends(updatedFriendsArray);
  }

  const arrayDataItems = friends.map((course) => (
    <li key={course.id}>
      <p>{course.name}</p>
      <span>{course.birthday}</span>
    </li> ))

  return (
    <>
      <main>
        <h1>This is frieds component.</h1>
        <NewFriendForm onAddFriend={handleAddFriend} />
        <ul>{arrayDataItems}</ul>
      </main>
    </>
  )

};

export default Friends;
