import { useEffect, useState } from "react";

function NewMeetup() {
  const [activities, setActivities] = useState([])

  const [date, setDate] = useState("");
  const [activityId, setActivityId] = useState("");
  const [errors, setErrors] = useState([]);

  const [dropdownDataFriends, setDropdownDataFriends] = useState([])
  const [selectedFriendId, setSelectedFriendId] = useState('');

  useEffect(() => {
    fetch("/activities")
      .then((r) => r.json())
      .then(setActivities);
  }, []);


  useEffect(() =>{
    fetch("/friends")
      .then(r => r.json())
      .then(data => setDropdownDataFriends(data))
      .catch(error => console.error(error))
  }, [])
  console.log(dropdownDataFriends)

  function handleSubmit(e) {
    e.preventDefault();
    const formData = {
            activity_id: Number(activityId),
            friend_id: Number(selectedFriendId),
            date: date,
    };
    fetch("/meetups", {
      method: "POST",
      headers: {"Content-Type": "application/json",},
      body: JSON.stringify(formData),
    }).then((r) => {
      if (r.ok) {
        r.json().then((activity) => {
          setDate("");
          setActivityId("");
          setErrors([]);
          onAddActivity(activity);
        });
      } else {
        r.json().then((err) => setErrors(err.errors));
      }
    });
  }

  return (
    <>
    <h1>Hello World Meetup</h1>

    <h1>Select a friend</h1>
